import React, {useState, useEffect} from 'react';
import Card from './Card';

function CardList({pokemons, currentPage, useRef}) {
	const [selectedId, setSelectedId] = useState();
	const skipFirstEffect = useRef(false);
    useEffect(() => {
    	if (skipFirstEffect.current){
			pokemons.splice(selectedId, 1);
		} else{
			skipFirstEffect.current = true;
		}
	}, [pokemons, selectedId])

	return (
		<div>
			{pokemons.map((user, index) => 
				<Card 
					key={pokemons[index].id} 
					name={pokemons[index].name} 
					img ={pokemons[index].img}
					currentPage={currentPage}
					handleDelete = {() => setSelectedId(index)}
				/>
			)}
		</div>
	);
};

export default CardList;
