import React, {useState} from 'react';
import Card from '../components/Card/Card';
import CardList from '../components/Card/CardList';
import { pokemons } from '../components/Pokemon';
import SearchBox from '../components/SearchBox';
import LookupBox from '../components/LookupBox';
// import './App.css';
import Scroll from '../components/Scroll';
import Clock from '../components/Clock';
import PokemonTitle from '../components/ApplicationName/PokemonTitle';
import useFetch from '../components/useFetch';


document.title = "Pokemon Library";
// class App extends Component {
// 	constructor() {
// 		super();
// 		this.state = {
// 			pokemons: pokemons,
// 			searchfield: ''
// 		};
// 	}

function App() {
	const [pokemonsArray, setPokemons] = useState(pokemons);
	const [searchField, setSearchField] = useState('');
	const [currentPage, setCurrentPage] = useState(true);
	const [id, setId] = useState(21);
	const [searchDebounce, setSearchDebounce] = useState('');
	const [displayData, setDisplayData] = useState('');
	// const onSearchChange = (event) =>{
	// 	setSearchField(event.target.value);
	// }

	async function handleSearch(event){
		// event.preventDefault();
		setSearchDebounce(searchField);
	}


	function onKeyDetect(event){
		if(event.key === 'Enter'){
			handleSearch();
		}
	}

	const {notFound, data, error} = useFetch(
    	`https://pokeapi.co/api/v2/pokemon/${searchDebounce}`, 
  	);


	// render() {
	const filterPokemons = pokemonsArray.filter(pokemon => {
		return pokemon.name.toLowerCase().includes(searchField.toLowerCase());
	})

	if (currentPage)
		return(
			<div>
				<div>
					<Clock className='db'/>

				</div>
				<div className = 'tc'>
					<PokemonTitle/>
					<nav className='overflow-auto'>
						<SearchBox 
							onChange={(e) => setSearchField(e.target.value)}
						/>
						<button 
							className='fr dib dim pointer f2 bg-moon-gray br3'
							onClick={() => (
								setCurrentPage(false),
								setSearchField('')
							)}
						>
							ADD+
						</button>
					</nav>
					<Scroll>
						<CardList pokemons={filterPokemons} />
					</Scroll>
				</div>
			</div>
		)
	return (
		<div>
			<div>
				<Clock className='db'/>
			</div>
			<div className = 'tc'>
				<PokemonTitle/>
				<h2>Add New Pokemon to page</h2>
				{/*accept input here and retrieve data from api*/}
				<LookupBox 
					onChange={(e) => setSearchField(e.target.value)}
					onKeyDetect={onKeyDetect}
					onSubmit={handleSearch}
				/>
				{notFound?
					<p>Pokemon is NOT FOUND!</p>
					:
					<img src={data.sprites.other["official-artwork"].front_default} alt="No images"></img>
				}
				<button 
					className='fr dib dim pointer f2 bg-moon-gray br3'
					onClick={() => {
						setCurrentPage(true)
						setSearchField('')
						setDisplayData('')
						this.setState({notFound:false})
					}}
				>Back
				</button>
			</div>
		</div>

	)
};

export default App;
