import React, {Component} from 'react';
import CardList from './CardList';
import { pokemons } from './Pokemon';
import SearchBox from './SearchBox';


class App extends Component {
	constructor() {
		super();
		this.state = {
			pokemons: pokemons,
			searchfield: ''
		};
	}

	searchChange(event){
		const filter = this.state.pokemons.filter(pokemons => {
			return pokemons.name.toLowerCase().includes(searchfield.toLowerCase());
		})
	}

	render() {
		return(
			<div className = 'tc'>
				<h1> Pokemon Index </h1>
				<SearchBox/>
				<CardList pokemons={this.state.pokemons} />
			</div>
		);
	}
};

export default App;
