import React, {useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
// import App from './containers/App';
import * as serviceWorker from './serviceWorker';
import 'tachyons';

function useFetch(uri) {
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();

  useEffect(() => {
    if (!uri) return;
    fetch(uri)
    .then(data => data.json())
    .then(setData)
    .then(() => setLoading(false))
    .catch(setError)
  }, [uri]);
  // console.log(data);
  return {loading, data, error};
}


function App({pokemonName}) {
  const {loading, data, error} = useFetch(
    `https://pokeapi.co/api/v2/pokemon/${pokemonName}`
  );
  if (loading) return <h1> loading...</h1>
  if (error)
    return <pre>{JSON.stringify(error, null, 2)}</pre>
  console.log(JSON.stringify(data.sprites.other["official-artwork"], null, 2))
  return (
    <div>
      {/*<pre>{JSON.stringify(data, null, 2)}</pre>*/}
      {/*// <img src={data.avatar_url} alt="No images"></img>*/}
      <h1>{data.login}</h1>
      {/*this condition to check if data.name exists.    */}
      {data.name && <p>{data.name}</p>}
      {/*{data.sprites && <p>{data.sprites}</p>}*/}
      <pre>{JSON.stringify(data.sprites.other["official-artwork"].front_default, null, 2)}</pre>
    </div>
  )

}


ReactDOM.render(
  <React.StrictMode>
  	<App pokemonName="bulbasaur"/>
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
